import React, { useState } from "react";

function LoginPage({ onLogin }) {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    if (typeof onLogin === "function") onLogin({ email });
  };

  return (
    <div style={styles.page}>
      <div style={styles.card}>
        <h1 style={styles.title}>EduPlay AI</h1>
        <p style={styles.subtitle}>
          Sign in to access quizzes, badges, and leaderboards
        </p>

        <form onSubmit={handleSubmit} style={styles.form}>
          <label style={styles.label}>Email</label>
          <input
            type="email"
            style={styles.input}
            placeholder="you@example.com"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />

          <label style={styles.label}>Password</label>
          <input
            type="password"
            style={styles.input}
            placeholder="********"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />

          <button type="submit" style={styles.button}>
            Sign in
          </button>
        </form>

        <p style={styles.footer}>
          Donâ€™t have an account?{" "}
          <a href="/register" style={styles.link}>
            Create one
          </a>
        </p>
      </div>
    </div>
  );
}

const styles = {
  page: {
    minHeight: "100vh",
    background: "#0f172a",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    fontFamily: "system-ui, -apple-system, Segoe UI, sans-serif",
  },
  card: {
    background: "#fff",
    width: "360px",
    padding: "28px 28px 32px",
    borderRadius: "18px",
    boxShadow: "0 20px 40px rgba(0,0,0,0.18)",
  },
  title: {
    textAlign: "center",
    fontSize: "1.4rem",
    fontWeight: 700,
    marginBottom: "4px",
    color: "#0f172a",
  },
  subtitle: {
    textAlign: "center",
    fontSize: "0.8rem",
    color: "#64748b",
    marginBottom: "20px",
  },
  form: {
    display: "flex",
    flexDirection: "row",
    flexWrap: "wrap",
    gap: "10px",
  },
  label: {
    fontSize: "0.75rem",
    fontWeight: 600,
    color: "#0f172a",
    width: "100%",
  },
  input: {
    padding: "9px 10px",
    borderRadius: "10px",
    border: "1px solid #e2e8f0",
    background: "#f8fafc",
    fontSize: "0.8rem",
    width: "100%",
  },
  button: {
    marginTop: "6px",
    padding: "10px 0",
    background: "#2563eb",
    border: "none",
    color: "white",
    fontWeight: 600,
    borderRadius: "10px",
    cursor: "pointer",
    width: "100%",
  },
  footer: {
    textAlign: "center",
    marginTop: "16px",
    fontSize: "0.7rem",
    color: "#94a3b8",
  },
  link: {
    color: "#2563eb",
    textDecoration: "none",
    fontWeight: 500,
  },
};

export default LoginPage;
