import React from "react";
import { useNavigate } from "react-router-dom";

export default function HomePage({ user, onStartGame, onLogout }) {
  const navigate = useNavigate();

  return (
    <div style={styles.page}>
      <header style={styles.header}>
        <div>
          <h1 style={styles.logo}>Educative AI</h1>
          <span style={styles.beta}>Prototype v0.1</span>
        </div>
        <div style={styles.headerRight}>
          <span style={styles.user}>Hi, {user?.email || "Guest"}</span>
          <button style={styles.outlineBtn} onClick={onLogout}>
            Log out
          </button>
        </div>
      </header>

      <main style={styles.main}>
        <section style={styles.hero}>
          <h2 style={styles.heroTitle}>
            Turn any topic into a playable quiz in seconds
          </h2>
          <p style={styles.heroText}>
            Paste notes, upload a PDF, or pick a subject. Our AI generates
            questions, auto-grades answers, and tracks class progress.
          </p>
          <div style={styles.heroActions}>
            <button
              style={styles.primaryBtn}
              onClick={() => navigate("/generate-quiz")}
            >
              From document (PDF)
            </button>
            <button style={styles.secondaryBtn} onClick={onStartGame}>
              Host live buzzer game
            </button>
          </div>
        </section>
      </main>
    </div>
  );
}

const styles = {
  page: {
    minHeight: "100vh",
    background: "#020617",
    color: "#e2e8f0",
    fontFamily: "system-ui, -apple-system, Segoe UI, sans-serif",
  },
  header: {
    display: "flex",
    justifyContent: "space-between",
    alignItems: "center",
    padding: "18px 40px",
    borderBottom: "1px solid rgba(148,163,184,0.3)",
  },
  logo: {
    fontSize: "1.3rem",
    fontWeight: 700,
  },
  beta: {
    fontSize: "0.7rem",
    color: "#64748b",
  },
  headerRight: {
    display: "flex",
    gap: "12px",
    alignItems: "center",
  },
  user: {
    fontSize: "0.85rem",
    color: "#cbd5f5",
  },
  outlineBtn: {
    padding: "6px 12px",
    borderRadius: "999px",
    border: "1px solid #4b5563",
    background: "transparent",
    color: "#e5e7eb",
    fontSize: "0.8rem",
    cursor: "pointer",
  },
  main: {
    padding: "40px",
    display: "flex",
    justifyContent: "center",
  },
  hero: {
    maxWidth: "720px",
    textAlign: "left",
  },
  heroTitle: {
    fontSize: "2.4rem",
    fontWeight: 700,
    marginBottom: "14px",
  },
  heroText: {
    fontSize: "1rem",
    color: "#9ca3af",
    marginBottom: "26px",
  },
  heroActions: {
    display: "flex",
    gap: "12px",
  },
  primaryBtn: {
    padding: "12px 18px",
    borderRadius: "999px",
    border: "none",
    background: "#2563eb",
    color: "white",
    fontWeight: 600,
    cursor: "pointer",
  },
  secondaryBtn: {
    padding: "12px 18px",
    borderRadius: "999px",
    border: "1px solid #4b5563",
    background: "transparent",
    color: "#e5e7eb",
    fontWeight: 500,
    cursor: "pointer",
  },
};
