import React, { useState, useEffect, useCallback, useRef } from 'react';
import { io } from 'socket.io-client';

// ==================== CONFIG ====================
const API_URL = process.env.REACT_APP_API_URL || 'http://localhost:3001';

// ==================== STYLES ====================
const styles = `
  @import url('https://fonts.googleapis.com/css2?family=Orbitron:wght@400;500;600;700;900&family=Outfit:wght@300;400;500;600;700&display=swap');

  :root {
    --bg-primary: #0a0a0f;
    --bg-secondary: #12121a;
    --bg-card: #1a1a24;
    --bg-card-hover: #22222e;
    --accent-cyan: #00f5d4;
    --accent-purple: #9b5de5;
    --accent-coral: #ff6b6b;
    --accent-gold: #ffd93d;
    --accent-blue: #4cc9f0;
    --text-primary: #ffffff;
    --text-secondary: #a0a0b0;
    --text-muted: #606070;
    --border-color: #2a2a3a;
    --success: #00f5a0;
    --error: #ff4757;
    --gradient-main: linear-gradient(135deg, #00f5d4 0%, #9b5de5 100%);
    --gradient-fire: linear-gradient(135deg, #ff6b6b 0%, #ffd93d 100%);
    --shadow-glow: 0 0 40px rgba(0, 245, 212, 0.15);
    --shadow-card: 0 8px 32px rgba(0, 0, 0, 0.4);
  }

  * {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
  }

  body {
    font-family: 'Outfit', sans-serif;
    background: var(--bg-primary);
    color: var(--text-primary);
    min-height: 100vh;
    overflow-x: hidden;
  }

  .app-container {
    min-height: 100vh;
    background: 
      radial-gradient(ellipse at 20% 0%, rgba(155, 93, 229, 0.08) 0%, transparent 50%),
      radial-gradient(ellipse at 80% 100%, rgba(0, 245, 212, 0.08) 0%, transparent 50%),
      var(--bg-primary);
    position: relative;
  }

  .app-container::before {
    content: '';
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background-image: 
      linear-gradient(rgba(255,255,255,0.02) 1px, transparent 1px),
      linear-gradient(90deg, rgba(255,255,255,0.02) 1px, transparent 1px);
    background-size: 50px 50px;
    pointer-events: none;
    z-index: 0;
  }

  .font-display { font-family: 'Orbitron', sans-serif; }

  /* Navigation */
  .navbar {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    height: 70px;
    background: rgba(10, 10, 15, 0.95);
    backdrop-filter: blur(20px);
    border-bottom: 1px solid var(--border-color);
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 0 40px;
    z-index: 1000;
  }

  .logo {
    font-family: 'Orbitron', sans-serif;
    font-size: 1.5rem;
    font-weight: 700;
    background: var(--gradient-main);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    display: flex;
    align-items: center;
    gap: 10px;
    cursor: pointer;
  }

  .logo-icon {
    width: 40px;
    height: 40px;
    background: var(--gradient-main);
    border-radius: 10px;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.2rem;
    -webkit-text-fill-color: var(--bg-primary);
  }

  .nav-user {
    display: flex;
    align-items: center;
    gap: 15px;
  }

  .user-name {
    color: var(--text-secondary);
    font-weight: 500;
  }

  .user-avatar {
    width: 42px;
    height: 42px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: 600;
    font-size: 1.1rem;
  }

  /* Main Content */
  .main-content {
    padding-top: 90px;
    padding-bottom: 40px;
    min-height: 100vh;
    position: relative;
    z-index: 1;
  }

  .container {
    max-width: 1200px;
    margin: 0 auto;
    padding: 0 40px;
  }

  .container-sm {
    max-width: 600px;
    margin: 0 auto;
    padding: 0 20px;
  }

  /* Page Header */
  .page-header {
    text-align: center;
    margin-bottom: 40px;
  }

  .page-title {
    font-family: 'Orbitron', sans-serif;
    font-size: 2.5rem;
    font-weight: 700;
    margin-bottom: 8px;
  }

  .page-subtitle {
    color: var(--text-secondary);
    font-size: 1.1rem;
  }

  /* Cards */
  .card {
    background: var(--bg-card);
    border-radius: 20px;
    border: 1px solid var(--border-color);
    padding: 32px;
    transition: all 0.3s ease;
  }

  .card:hover {
    border-color: rgba(0, 245, 212, 0.3);
  }

  .card-title {
    font-family: 'Orbitron', sans-serif;
    font-size: 1.2rem;
    font-weight: 600;
    margin-bottom: 8px;
  }

  /* Buttons */
  .btn {
    padding: 14px 28px;
    border-radius: 12px;
    font-weight: 600;
    font-size: 1rem;
    cursor: pointer;
    transition: all 0.3s ease;
    border: none;
    display: inline-flex;
    align-items: center;
    justify-content: center;
    gap: 10px;
    font-family: 'Outfit', sans-serif;
    text-decoration: none;
  }

  .btn:disabled {
    opacity: 0.5;
    cursor: not-allowed;
  }

  .btn-primary {
    background: var(--gradient-main);
    color: var(--bg-primary);
  }

  .btn-primary:hover:not(:disabled) {
    transform: translateY(-2px);
    box-shadow: 0 10px 30px rgba(0, 245, 212, 0.3);
  }

  .btn-secondary {
    background: var(--bg-card);
    color: var(--text-primary);
    border: 2px solid var(--border-color);
  }

  .btn-secondary:hover:not(:disabled) {
    border-color: var(--accent-cyan);
    background: var(--bg-card-hover);
  }

  .btn-danger {
    background: var(--accent-coral);
    color: white;
  }

  .btn-lg {
    padding: 18px 36px;
    font-size: 1.1rem;
  }

  .btn-block {
    width: 100%;
  }

  /* Input Fields */
  .input-group {
    margin-bottom: 24px;
  }

  .input-label {
    display: block;
    margin-bottom: 8px;
    font-weight: 500;
    color: var(--text-secondary);
    font-size: 0.95rem;
  }

  .input {
    width: 100%;
    padding: 16px 20px;
    background: var(--bg-secondary);
    border: 2px solid var(--border-color);
    border-radius: 12px;
    color: var(--text-primary);
    font-size: 1rem;
    font-family: 'Outfit', sans-serif;
    transition: all 0.3s ease;
  }

  .input:focus {
    outline: none;
    border-color: var(--accent-cyan);
    box-shadow: 0 0 20px rgba(0, 245, 212, 0.1);
  }

  .input::placeholder {
    color: var(--text-muted);
  }

  .input-code {
    text-align: center;
    font-size: 2rem;
    letter-spacing: 12px;
    font-family: 'Orbitron', sans-serif;
    text-transform: uppercase;
  }

  /* File Upload */
  .file-upload {
    border: 2px dashed var(--border-color);
    border-radius: 16px;
    padding: 40px;
    text-align: center;
    cursor: pointer;
    transition: all 0.3s ease;
    background: var(--bg-secondary);
  }

  .file-upload:hover {
    border-color: var(--accent-cyan);
    background: rgba(0, 245, 212, 0.05);
  }

  .file-upload.has-file {
    border-color: var(--success);
    background: rgba(0, 245, 160, 0.05);
  }

  .file-upload-icon {
    font-size: 3rem;
    margin-bottom: 16px;
  }

  .file-upload-text {
    color: var(--text-secondary);
    margin-bottom: 8px;
  }

  .file-upload-hint {
    color: var(--text-muted);
    font-size: 0.85rem;
  }

  .file-name {
    color: var(--success);
    font-weight: 600;
    margin-top: 12px;
  }

  /* Game Code Display */
  .game-code-display {
    background: var(--bg-secondary);
    border-radius: 16px;
    padding: 30px;
    text-align: center;
    margin: 30px 0;
  }

  .game-code {
    font-family: 'Orbitron', sans-serif;
    font-size: 3rem;
    font-weight: 700;
    letter-spacing: 12px;
    color: var(--accent-cyan);
    margin-bottom: 12px;
  }

  .game-code-label {
    color: var(--text-secondary);
    font-size: 0.95rem;
  }

  /* Lobby */
  .lobby-info {
    text-align: center;
    margin-bottom: 30px;
  }

  .lobby-title {
    font-family: 'Orbitron', sans-serif;
    font-size: 1.8rem;
    margin-bottom: 8px;
  }

  .lobby-topic {
    color: var(--text-secondary);
  }

  .players-grid {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(200px, 1fr));
    gap: 16px;
    margin: 30px 0;
  }

  .player-card {
    background: var(--bg-secondary);
    border-radius: 16px;
    padding: 20px;
    text-align: center;
    border: 2px solid var(--border-color);
    transition: all 0.3s ease;
  }

  .player-card.host {
    border-color: var(--accent-gold);
  }

  .player-card.ready {
    border-color: var(--success);
  }

  .player-avatar {
    width: 60px;
    height: 60px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-size: 1.5rem;
    font-weight: 700;
    margin: 0 auto 12px;
  }

  .player-name {
    font-weight: 600;
    margin-bottom: 4px;
  }

  .player-status {
    font-size: 0.85rem;
    color: var(--text-muted);
  }

  .player-status.host {
    color: var(--accent-gold);
  }

  .player-status.ready {
    color: var(--success);
  }

  /* Quiz Interface */
  .quiz-container {
    max-width: 800px;
    margin: 0 auto;
  }

  .quiz-header {
    display: flex;
    align-items: center;
    justify-content: space-between;
    margin-bottom: 30px;
    padding: 20px 24px;
    background: var(--bg-card);
    border-radius: 16px;
    border: 1px solid var(--border-color);
  }

  .quiz-progress {
    display: flex;
    align-items: center;
    gap: 12px;
  }

  .progress-bar {
    width: 150px;
    height: 8px;
    background: var(--bg-secondary);
    border-radius: 4px;
    overflow: hidden;
  }

  .progress-fill {
    height: 100%;
    background: var(--gradient-main);
    border-radius: 4px;
    transition: width 0.5s ease;
  }

  .quiz-timer {
    font-family: 'Orbitron', sans-serif;
    font-size: 1.8rem;
    font-weight: 700;
    color: var(--accent-coral);
    min-width: 80px;
    text-align: center;
  }

  .quiz-timer.warning {
    animation: timerPulse 0.5s infinite;
  }

  @keyframes timerPulse {
    0%, 100% { color: var(--accent-coral); transform: scale(1); }
    50% { color: var(--accent-gold); transform: scale(1.1); }
  }

  .quiz-score {
    font-family: 'Orbitron', sans-serif;
    font-size: 1.2rem;
    color: var(--accent-gold);
  }

  .quiz-question-card {
    background: var(--bg-card);
    border-radius: 24px;
    border: 1px solid var(--border-color);
    padding: 40px;
    margin-bottom: 30px;
  }

  .question-number {
    font-family: 'Orbitron', sans-serif;
    font-size: 0.9rem;
    color: var(--accent-cyan);
    margin-bottom: 16px;
    text-transform: uppercase;
    letter-spacing: 2px;
  }

  .question-text {
    font-size: 1.4rem;
    font-weight: 500;
    line-height: 1.6;
  }

  .answer-options {
    display: flex;
    flex-direction: column;
    gap: 16px;
  }

  .answer-option {
    padding: 20px 24px;
    background: var(--bg-secondary);
    border: 2px solid var(--border-color);
    border-radius: 16px;
    cursor: pointer;
    transition: all 0.3s ease;
    display: flex;
    align-items: center;
    gap: 16px;
    font-size: 1.05rem;
    text-align: left;
  }

  .answer-option:hover:not(.disabled) {
    border-color: var(--accent-cyan);
    background: rgba(0, 245, 212, 0.05);
    transform: translateX(8px);
  }

  .answer-option.selected {
    border-color: var(--accent-cyan);
    background: rgba(0, 245, 212, 0.1);
  }

  .answer-option.correct {
    border-color: var(--success);
    background: rgba(0, 245, 160, 0.15);
  }

  .answer-option.incorrect {
    border-color: var(--error);
    background: rgba(255, 71, 87, 0.15);
  }

  .answer-option.disabled {
    cursor: not-allowed;
    opacity: 0.7;
  }

  .answer-letter {
    width: 44px;
    height: 44px;
    border-radius: 12px;
    background: var(--bg-card);
    display: flex;
    align-items: center;
    justify-content: center;
    font-family: 'Orbitron', sans-serif;
    font-weight: 600;
    flex-shrink: 0;
  }

  .answer-option.selected .answer-letter {
    background: var(--accent-cyan);
    color: var(--bg-primary);
  }

  .answer-option.correct .answer-letter {
    background: var(--success);
    color: var(--bg-primary);
  }

  .answer-option.incorrect .answer-letter {
    background: var(--error);
    color: white;
  }

  /* Live Scoreboard */
  .live-scoreboard {
    position: fixed;
    right: 30px;
    top: 100px;
    width: 280px;
    background: var(--bg-card);
    border-radius: 20px;
    border: 1px solid var(--border-color);
    padding: 20px;
    z-index: 100;
  }

  .scoreboard-title {
    font-family: 'Orbitron', sans-serif;
    font-size: 0.85rem;
    color: var(--accent-cyan);
    margin-bottom: 16px;
    display: flex;
    align-items: center;
    gap: 8px;
    text-transform: uppercase;
    letter-spacing: 1px;
  }

  .live-indicator {
    width: 8px;
    height: 8px;
    border-radius: 50%;
    background: var(--accent-coral);
    animation: pulse 1s infinite;
  }

  @keyframes pulse {
    0%, 100% { opacity: 1; transform: scale(1); }
    50% { opacity: 0.5; transform: scale(0.9); }
  }

  .scoreboard-player {
    display: flex;
    align-items: center;
    gap: 12px;
    padding: 12px;
    border-radius: 12px;
    margin-bottom: 8px;
    transition: all 0.3s ease;
    background: var(--bg-secondary);
  }

  .scoreboard-player.you {
    background: rgba(0, 245, 212, 0.1);
    border: 1px solid rgba(0, 245, 212, 0.3);
  }

  .scoreboard-player.first {
    background: rgba(255, 217, 61, 0.1);
  }

  .scoreboard-rank {
    font-family: 'Orbitron', sans-serif;
    font-weight: 700;
    font-size: 1rem;
    width: 28px;
    color: var(--text-muted);
  }

  .scoreboard-rank.gold { color: var(--accent-gold); }
  .scoreboard-rank.silver { color: #c0c0c0; }
  .scoreboard-rank.bronze { color: #cd7f32; }

  .scoreboard-avatar {
    width: 36px;
    height: 36px;
    border-radius: 50%;
    display: flex;
    align-items: center;
    justify-content: center;
    font-weight: 600;
    font-size: 0.9rem;
  }

  .scoreboard-name {
    flex: 1;
    font-weight: 500;
    font-size: 0.95rem;
  }

  .scoreboard-score {
    font-family: 'Orbitron', sans-serif;
    font-weight: 600;
    color: var(--accent-cyan);
  }

  /* Results */
  .results-container {
    text-align: center;
    padding: 40px 20px;
  }

  .results-trophy {
    font-size: 6rem;
    margin-bottom: 24px;
    animation: bounce 1s ease infinite;
  }

  @keyframes bounce {
    0%, 100% { transform: translateY(0); }
    50% { transform: translateY(-15px); }
  }

  .results-position {
    font-family: 'Orbitron', sans-serif;
    font-size: 1.2rem;
    color: var(--accent-cyan);
    margin-bottom: 8px;
    text-transform: uppercase;
    letter-spacing: 3px;
  }

  .results-title {
    font-family: 'Orbitron', sans-serif;
    font-size: 2.5rem;
    margin-bottom: 40px;
  }

  .results-score-display {
    font-family: 'Orbitron', sans-serif;
    font-size: 5rem;
    font-weight: 700;
    background: var(--gradient-main);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    margin-bottom: 40px;
  }

  .results-stats {
    display: flex;
    justify-content: center;
    gap: 50px;
    margin-bottom: 50px;
  }

  .result-stat {
    text-align: center;
  }

  .result-stat-value {
    font-family: 'Orbitron', sans-serif;
    font-size: 2rem;
    font-weight: 600;
    margin-bottom: 4px;
  }

  .result-stat-label {
    color: var(--text-secondary);
    font-size: 0.9rem;
  }

  .final-leaderboard {
    max-width: 500px;
    margin: 0 auto 40px;
  }

  .final-leaderboard-title {
    font-family: 'Orbitron', sans-serif;
    font-size: 1rem;
    color: var(--accent-cyan);
    margin-bottom: 20px;
    text-transform: uppercase;
    letter-spacing: 2px;
  }

  /* Loading States */
  .loading-container {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    padding: 80px 20px;
  }

  .loading-spinner {
    width: 50px;
    height: 50px;
    border: 3px solid var(--border-color);
    border-top-color: var(--accent-cyan);
    border-radius: 50%;
    animation: spin 1s linear infinite;
    margin-bottom: 20px;
  }

  @keyframes spin {
    to { transform: rotate(360deg); }
  }

  .loading-text {
    color: var(--text-secondary);
    font-size: 1.1rem;
  }

  /* Error States */
  .error-message {
    background: rgba(255, 71, 87, 0.1);
    border: 1px solid var(--error);
    border-radius: 12px;
    padding: 16px 20px;
    color: var(--error);
    margin-bottom: 20px;
    text-align: center;
  }

  /* Countdown */
  .countdown-overlay {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    bottom: 0;
    background: rgba(10, 10, 15, 0.95);
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    z-index: 2000;
  }

  .countdown-number {
    font-family: 'Orbitron', sans-serif;
    font-size: 12rem;
    font-weight: 900;
    background: var(--gradient-main);
    -webkit-background-clip: text;
    -webkit-text-fill-color: transparent;
    background-clip: text;
    animation: countdownPulse 1s ease infinite;
  }

  @keyframes countdownPulse {
    0% { transform: scale(0.8); opacity: 0.5; }
    50% { transform: scale(1.1); opacity: 1; }
    100% { transform: scale(1); opacity: 0.8; }
  }

  .countdown-text {
    font-family: 'Orbitron', sans-serif;
    font-size: 1.5rem;
    color: var(--text-secondary);
    margin-top: 20px;
  }

  /* Responsive */
  @media (max-width: 1200px) {
    .live-scoreboard { display: none; }
  }

  @media (max-width: 768px) {
    .navbar { padding: 0 20px; }
    .container { padding: 0 20px; }
    .page-title { font-size: 1.8rem; }
    .quiz-question-card { padding: 24px; }
    .question-text { font-size: 1.2rem; }
    .game-code { font-size: 2rem; letter-spacing: 8px; }
    .results-score-display { font-size: 3rem; }
    .results-stats { gap: 30px; }
  }
`;

// ==================== SOCKET CONNECTION ====================
let socket = null;

function connectSocket() {
  if (!socket) {
    socket = io(API_URL, {
      transports: ['websocket', 'polling'],
    });
  }
  return socket;
}

// ==================== COMPONENTS ====================

// Navigation
const Navbar = ({ user, onLogoClick }) => (
  <nav className="navbar">
    <div className="logo" onClick={onLogoClick}>
      <div className="logo-icon">ðŸ§ </div>
      EDUCATIVE AI
    </div>
    {user && (
      <div className="nav-user">
        <span className="user-name">{user.name}</span>
        <div className="user-avatar" style={{ background: user.color || 'var(--gradient-main)' }}>
          {user.avatar}
        </div>
      </div>
    )}
  </nav>
);

// Home Screen - Choose Create or Join
const HomeScreen = ({ onCreateGame, onJoinGame }) => (
  <div className="container-sm">
    <div className="page-header">
      <h1 className="page-title">ðŸ§  Educative AI</h1>
      <p className="page-subtitle">Challenge your friends to AI-powered quiz battles!</p>
    </div>

    <div className="card" style={{ marginBottom: '20px', cursor: 'pointer' }} onClick={onCreateGame}>
      <div style={{ textAlign: 'center', padding: '20px 0' }}>
        <div style={{ fontSize: '4rem', marginBottom: '16px' }}>ðŸŽ¯</div>
        <h3 className="card-title">Create Game</h3>
        <p style={{ color: 'var(--text-secondary)' }}>
          Upload a PDF and invite friends to compete
        </p>
      </div>
    </div>

    <div className="card" style={{ cursor: 'pointer' }} onClick={onJoinGame}>
      <div style={{ textAlign: 'center', padding: '20px 0' }}>
        <div style={{ fontSize: '4rem', marginBottom: '16px' }}>ðŸš€</div>
