import React, { useState } from "react";
import { Routes, Route, useNavigate } from "react-router-dom";

import LoginPage from "./LoginPage";
import HomePage from "./HomePage";
import GenerateQuizPage from "./GenerateQuizPage";
import GamePage from "./GamePage";

export default function App() {
  const [user, setUser] = useState(null);
  const navigate = useNavigate();

  // When login is successful → save user + go to home
  const handleLogin = (payload) => {
    setUser({ email: payload?.email || "teacher@example.com" });
    navigate("/home");
  };

  // "New game" button on HomePage → go to GamePage
  const handleStartGame = () => {
    navigate("/game");
  };

  // "Generate quiz" button on HomePage → go to GenerateQuizPage
  const handleGenerateQuiz = () => {
    navigate("/generate-quiz");
  };

  // Logout → clear user + go back to login
  const handleLogout = () => {
    setUser(null);
    navigate("/");
  };

  return (
    <Routes>
      <Route path="/" element={<LoginPage onLogin={handleLogin} />} />

      <Route
        path="/home"
        element={
          <HomePage
            user={user}
            onStartGame={handleStartGame}
            onGenerateQuiz={handleGenerateQuiz}
            onLogout={handleLogout}
          />
        }
      />

      <Route path="/generate-quiz" element={<GenerateQuizPage />} />

      <Route path="/game" element={<GamePage user={user} />} />
    </Routes>
  );
}
